document.addEventListener("DOMContentLoaded",()=>{try{if(window.lucide)lucide.createIcons()}catch(error){console.error("Errore nell'inizializzazione di Lucide:",error)}try{if(window.AOS){AOS.init({duration:700,once:true,offset:50})}}catch(error){console.error("Errore nell'inizializzazione di AOS:",error)}const mobileMenuButton=document.getElementById("mobile-menu-button");const mobileMenu=document.getElementById("mobile-menu");if(mobileMenuButton&&mobileMenu){mobileMenuButton.addEventListener("click",()=>{mobileMenu.classList.toggle("hidden");mobileMenuButton.setAttribute("aria-expanded",mobileMenu.classList.contains("hidden")?"false":"true")})}const allNavLinks=document.querySelectorAll("#mobile-menu a");allNavLinks.forEach(link=>{link.addEventListener("click",()=>{if(mobileMenu)mobileMenu.classList.add("hidden")})})});